from json import dump
from os import path
from itertools import combinations, filterfalse


baseFolder = path.dirname(__file__)
enemies = []
enemyIds = {}
enemiesDict = {}
reach = []

newGamePlusMods = [
    "dodge1", "dodge2",
    "damage1", "damage2", "damage3", "damage4",
    "armor resist1",
    "armor1", "armor2",
    "resist1", "resist2",
    "health1", "health2", "health3", "health4",
    "damage health1", "damage health2",
    "repeat",
    "magic", "physical",
    "bleed", "frostbite", "poison", "stagger"
]

ngpc = []
for x in range(1, 5):
    ngpc += filterfalse(lambda c: len(c) != len(set([a[:-1] for a in c])) or len(set(c) & {"bleed", "frostbite", "poison", "stagger"}) > 2, combinations(newGamePlusMods, x))

class Enemy:
    def __init__(self, name, expansion, enemyType, numberOfModels, health, armor,resist, attacks, attackType, dodge, move, attackRange, openSlots=1, id=None, repeat=0, initialPushDamage=False, nodeAttack=[], nodesAttacked=[], attackEffect=[], weakArcs=None, windup=False, moveIndex=None, skipDefense=False, difficultyTiers=0, noExtraConditions=False, moveAttack=[], modified=False, comboSet=frozenset()) -> None:
        enemiesDict[name] = self
        enemies.append(self)

        if id:
            enemyIds[id] = self

        self.name = name
        self.id = id
        self.expansion = expansion
        self.enemyType = enemyType
        self.numberOfModels = numberOfModels
        self.health = health
        self.armor = armor
        self.resist = resist
        self.repeat = repeat
        self.initalPushDamage = initialPushDamage

        if not nodeAttack:
            nodeAttack = [False for _ in attacks]

        if not nodesAttacked:
            nodesAttacked = [0 for _ in attacks]

        if not attackEffect:
            attackEffect = [set() for _ in attacks]

        if not moveAttack:
            moveAttack = [False for _ in attacks]

        if initialPushDamage:
            # Get the index of the first move.
            # That's where we insert this push damage.
            moveIndex = move.index([m for m in move if m > 0][0]) if moveIndex is None else moveIndex
            attacks.insert(moveIndex, attacks[moveIndex])
            nodeAttack.insert(moveIndex, True)
            nodesAttacked.insert(moveIndex, 0)
            attackType.insert(moveIndex, attackType[moveIndex])
            attackRange.insert(moveIndex, 0)
            attackEffect.insert(moveIndex, attackEffect[moveIndex])
            move.insert(moveIndex, 0)
            moveAttack.insert(moveIndex, True)

        self.attacks = attacks * (repeat + 1)
        self.nodeAttack = nodeAttack * (repeat + 1)
        self.nodesAttacked = nodesAttacked * (repeat + 1)
        self.attackType = attackType * (repeat + 1)
        self.attackRange = attackRange * (repeat + 1)
        self.attackEffect = attackEffect * (repeat + 1)
        self.move = move * (repeat + 1)
        moveAttack = moveAttack * (repeat + 1)
        self.skipDefense = skipDefense

        if enemyType == "regular":
            self.attacks = self.attacks * 2
            self.nodeAttack = self.nodeAttack * 2
            self.nodesAttacked = self.nodesAttacked * 2
            self.attackType = self.attackType * 2
            self.attackRange = self.attackRange * 2
            self.attackEffect = self.attackEffect * 2
            self.move = self.move * 2
            moveAttack = moveAttack * 2

        self.dodge = dodge
        self.weakArcs = weakArcs if weakArcs is not None else 1 if "boss" in enemyType else 0
        self.windup = windup
        self.difficultyTiers = difficultyTiers
        self.modified = modified
        self.comboSet = comboSet

        check = set()
        check.add(len(self.attacks))
        check.add(len(self.attackRange))
        check.add(len(self.attackType))
        check.add(len(self.nodeAttack))
        check.add(len(self.nodesAttacked))
        check.add(len(moveAttack))
        if self.attackEffect:
            check.add(len(self.attackEffect))
        if len(set(check)) > 1 or set(self.attackType) - {"physical", "magic"}:
            raise
        
        if "Hollow" in self.name and self.health == 1:
            self.gang = "Hollow"
        elif "Alonne" in self.name and self.health == 1:
            self.gang = "Alonne"
        elif "Skeleton" in self.name and self.health == 1:
            self.gang = "Skeleton"
        elif "Silver Knight" in self.name and self.health == 1:
            self.gang = "Silver Knight"
        else:
            self.gang = None

        self.deaths = {
            1: 0,
            2: 0,
            3: 0
        }
        self.damageDone1 = {
            1: 0,
            2: 0,
            3: 0
        }
        self.damageDone2 = {
            1: 0,
            2: 0,
            3: 0
        }
        self.damageDone3 = {
            1: 0,
            2: 0,
            3: 0
        }
        self.damageDone4 = {
            1: 0,
            2: 0,
            3: 0
        }
        self.bleedDamage1 = {
            1: 0,
            2: 0,
            3: 0
        }
        self.bleedDamage2 = {
            1: 0,
            2: 0,
            3: 0
        }
        self.bleedDamage3 = {
            1: 0,
            2: 0,
            3: 0
        }
        self.bleedDamage4 = {
            1: 0,
            2: 0,
            3: 0
        }
        self.damagingAttacks = {
            1: 0,
            2: 0,
            3: 0
        }
        self.totalAttacks = {
            1: 0,
            2: 0,
            3: 0
        }
        self.loadoutDamage = {}

        for i, m in enumerate(self.move):
            reach.append(min([4, max([0, m + self.attackRange[i]])]))

        if not modified:
            for combo in ngpc:
                comboSet = frozenset(combo)
                if (
                    ("magic" in comboSet and "physical" not in attackType)
                    or ("physical" in comboSet and "magic" not in attackType)
                    or ("magic" in comboSet and not any([("physical", False) in set(zip(attackType, moveAttack))]))
                    or any([ae & comboSet for ae in attackEffect])
                    or (noExtraConditions and {"bleed", "frostbite", "poison", "stagger"} & comboSet)
                    or any([len(ae | ({"bleed", "frostbite", "poison", "stagger"} & comboSet)) > 2 for ae in attackEffect])
                    or ("armor resist1" in comboSet and {"armor1", "armor2", "resist1", "resist2"} & comboSet)
                    or ("damage health1" in comboSet and {"health1", "health2", "health3", "health4", "damage1", "damage2", "damage3", "damage4"} & comboSet)
                    or ("dodge1" in comboSet and dodge == 4)
                    or ("dodge2" in comboSet and dodge >= 3)
                    or (enemyType == "regular" and openSlots == 0 and comboSet & {"bleed", "frostbite", "poison", "stagger"} and not any([ae for ae in attackEffect]))
                    or (enemyType == "regular" and openSlots == 0 and "repeat" in comboSet and repeat == 0)
                    or (enemyType == "regular" and openSlots == 1 and comboSet & {"bleed", "frostbite", "poison", "stagger"} and not any([ae for ae in attackEffect]) and "repeat" in comboSet and repeat == 0)
                    or (name == "Executioner's Chariot - Death Race" and "repeat" in comboSet)
                    ):
                    continue

                Enemy(
                    name + " " + str(combo),
                    expansion,
                    enemyType,
                    numberOfModels,
                    health=health + (1 if {"health1", "damage health1"} & comboSet else 2 if {"health2", "damage health2"} & comboSet else 3 if "health3" in combo else 4 if "health4" in combo else 0),
                    armor=armor + (1 if "armor1" in combo or "armor resist1" in combo else 2 if "armor2" in combo else 0),
                    resist=resist + (1 if "resist1" in combo or "armor resist1" in combo else 2 if "resist2" in combo else 0),
                    attacks=[attack + (0 if attack == 0 else 1 if {"damage1", "damage health1"} & comboSet else 2 if {"damage2", "damage health2"} & comboSet else 3 if "damage3" in combo else 4 if "damage4" in combo else 0) for attack in attacks],
                    attackType=["magic" if "magic" in combo and not moveAttack[i] else "physical" if "physical" in combo or moveAttack[i] else at for i, at in enumerate(attackType)],
                    dodge=dodge + (1 if "dodge1" in combo else 0) + (2 if "dodge2" in combo else 0),
                    move=move,
                    attackRange=attackRange,
                    repeat=repeat + (1 if "repeat" in combo else 0),
                    id=max([enemy.id for enemy in enemies]) + 1 if id else None,
                    attackEffect=[ae | ({"bleed",} if "bleed" in set(combo) else set()) | ({"poison",} if "poison" in set(combo) else set()) | ({"frostbite",} if "frostbite" in set(combo) else set()) | ({"stagger",} if "stagger" in set(combo) else set()) for ae in attackEffect],
                    nodeAttack=nodeAttack,
                    nodesAttacked=nodesAttacked,
                    weakArcs=weakArcs,
                    windup=windup,
                    skipDefense=skipDefense,
                    modified=True,
                    comboSet=comboSet)


    def reset(self):
        with open(baseFolder + "\\enemies\\" + self.name + ".json", "w") as enemyFile:
            dump({"deaths": 0, "totalAttacks": 0, "damagingAttacks": 0, "damageDone": 0, "bleedDamage": 0, "loadoutDamage": {}}, enemyFile)


