from json import dump
from os import path
from itertools import combinations, filterfalse


baseFolder = path.dirname(__file__)
enemies = []
enemyIds = {}
enemiesDict = {}
reach = []
vordtStuff = []
guardianDragonStuff = []
pursuerStuff = []

newGamePlusMods = [
    "dodge1", "dodge2",
    "damage1", "damage2", "damage3", "damage4",
    "move1",
    "armor1", "armor2",
    "resist1", "resist2",
    "repeat",
    "magic",
    "bleed1", "frostbite1", "poison1", "stagger1",
    "bleed2", "frostbite2", "poison2", "stagger2",
    "health1", "health2", "health3", "health4", "health5"
]

ngpc = []
for x in range(1, 5):
    ngpc += filterfalse(lambda c: len(c) != len(set([a[:-1] for a in c])), combinations(newGamePlusMods, x))

class Enemy:
    def __init__(self, name, expansion, enemyType, numberOfModels, health, armor, resist, attacks, attackType, dodge, move, attackRange, id=None, repeat=0, initialPushDamage=False, nodeAttack=[], nodesAttacked=[], attackEffect=[], weakArcs=None, windup=False, toughness=0, difficultyTiers=0, modified=False) -> None:
        enemiesDict[name] = self
        enemies.append(self)

        if id:
            enemyIds[id] = self

        self.name = name
        self.id = id
        self.expansion = expansion
        self.enemyType = enemyType
        self.numberOfModels = numberOfModels
        self.health = health
        self.armor = armor
        self.resist = resist
        self.repeat = repeat
        self.initalPushDamage = initialPushDamage

        if not nodeAttack:
            nodeAttack = [False for _ in attacks]

        if not nodesAttacked:
            nodesAttacked = [0 for _ in attacks]

        if not attackEffect:
            attackEffect = [set() for _ in attacks]

        if initialPushDamage:
            attacks = attacks[0] + attacks
            nodeAttack = [True] + nodeAttack
            nodesAttacked = [0] + nodesAttacked
            attackType = attackType[0] + attackType
            attackRange = [0] + attackRange
            attackEffect = attackEffect[0] + attackEffect
            move = [0] + move

        if repeat:
            pass

        self.attacks = attacks * (repeat + 1)
        self.nodeAttack = nodeAttack * (repeat + 1)
        self.nodesAttacked = nodesAttacked * (repeat + 1)
        self.attackType = attackType * (repeat + 1)
        self.attackRange = attackRange * (repeat + 1)
        self.attackEffect = attackEffect * (repeat + 1)
        self.move = move * (repeat + 1)

        if enemyType == "regular":
            self.attacks = self.attacks * 2
            self.nodeAttack = self.nodeAttack * 2
            self.nodesAttacked = self.nodesAttacked * 2
            self.attackType = self.attackType * 2
            self.attackRange = self.attackRange * 2
            self.attackEffect = self.attackEffect * 2
            self.move = self.move * 2

        self.dodge = dodge
        self.weakArcs = weakArcs if weakArcs is not None else 1 if "boss" in enemyType else 0
        self.windup = windup
        self.difficultyTiers = difficultyTiers
        self.modified = modified
        # Lower is better
        self.toughness = toughness

        check = set()
        check.add(len(self.attacks))
        check.add(len(self.attackRange))
        check.add(len(self.attackType))
        check.add(len(self.nodeAttack))
        check.add(len(self.nodesAttacked))
        if self.attackEffect:
            check.add(len(self.attackEffect))
        if len(set(check)) > 1 or set(self.attackType) - {"physical", "magic"}:
            raise
        
        if "Hollow" in self.name and self.health == 1:
            self.gang = "Hollow"
        elif "Alonne" in self.name and self.health == 1:
            self.gang = "Alonne"
        elif "Skeleton" in self.name and self.health == 1:
            self.gang = "Skeleton"
        elif "Silver Knight" in self.name and self.health == 1:
            self.gang = "Silver Knight"
        else:
            self.gang = None

        self.deaths = {
            1: 0,
            2: 0,
            3: 0
        }
        self.damageDone1 = {
            1: 0,
            2: 0,
            3: 0
        }
        self.damageDone2 = {
            1: 0,
            2: 0,
            3: 0
        }
        self.damageDone3 = {
            1: 0,
            2: 0,
            3: 0
        }
        self.damageDone4 = {
            1: 0,
            2: 0,
            3: 0
        }
        self.bleedDamage1 = {
            1: 0,
            2: 0,
            3: 0
        }
        self.bleedDamage2 = {
            1: 0,
            2: 0,
            3: 0
        }
        self.bleedDamage3 = {
            1: 0,
            2: 0,
            3: 0
        }
        self.bleedDamage4 = {
            1: 0,
            2: 0,
            3: 0
        }
        self.damagingAttacks = {
            1: 0,
            2: 0,
            3: 0
        }
        self.totalAttacks = {
            1: 0,
            2: 0,
            3: 0
        }
        self.loadoutDamage = {}

        for i, m in enumerate(self.move):
            reach.append(min([4, max([0, m + self.attackRange[i]])]))

        if not modified:
            for combo in ngpc:
                if (
                    "magic" in combo and "magic" in attackType
                    or any([ae & set(combo) for ae in attackEffect])
                    ):
                    continue

                Enemy(
                    name + " " + str(combo),
                    expansion,
                    enemyType,
                    numberOfModels,
                    health=health + sum([(int(mod[-1]) if "dodge" in mod else 0) for mod in combo]),
                    armor=armor + sum([(int(mod[-1]) if "armor" in mod else 0) for mod in combo]),
                    resist=resist + sum([(int(mod[-1]) if "resist" in mod else 0) for mod in combo]),
                    attacks=[attack + (sum([(int(mod[-1]) if "damage" in mod else 0) for mod in combo]) if attack > 0 else 0) for attack in attacks],
                    attackType=["magic" for _ in attackType] if "magic" in combo else attackType,
                    dodge=dodge + sum([(int(mod[-1]) if "dodge" in mod else 0) for mod in combo]),
                    move=[m + (sum([(int(mod[-1]) if "move" in mod else 0) for mod in combo]) if m > 0 else 0) for m in move],
                    attackRange=attackRange,
                    repeat=1 if "repeat" in combo else 0,
                    id=max([enemy.id for enemy in enemies]) + 1 if id else None,
                    attackEffect=[ae | ({"bleed",} if {"bleed1", "bleed2"} & set(combo) else set()) | ({"poison",} if {"poison1", "poison2"} & set(combo) else set()) | ({"frostbite",} if {"frostbite1", "frostbite2"} & set(combo) else set()) | ({"stagger",} if {"stagger1", "stagger2"} & set(combo) else set()) for ae in attackEffect],
                    modified=True)


    def reset(self):
        with open(baseFolder + "\\enemies\\" + self.name + ".json", "w") as enemyFile:
            dump({"deaths": 0, "totalAttacks": 0, "damagingAttacks": 0, "damageDone": 0, "bleedDamage": 0, "loadoutDamage": {}}, enemyFile)


