from json import dump
from os import path
from itertools import product


baseFolder = path.dirname(__file__)
enemies = []
enemyIds = {}
enemiesDict = {}
reach = []
vordtStuff = []
guardianDragonStuff = []


class Enemy:
    def __init__(self, name, expansion, enemyType, numberOfModels, health, armor, resist, attacks, attackType, dodge, move, attackRange, id=None, skip=False, nodeAttack=[], nodesAttacked=0, attackEffect=[], weakArcs=None, windup=False, difficulty=0) -> None:
        if not skip:
            enemiesDict[name] = self
            enemies.append(self)
        elif "Vordt" in name:
            vordtStuff.append(self)
        
        if "Guardian Dragon" in name:
            guardianDragonStuff.append(self)

        if id:
            enemyIds[id] = self

        self.name = name
        self.id = id
        self.expansion = expansion
        self.enemyType = enemyType
        self.numberOfModels = numberOfModels
        self.health = health
        self.armor = armor
        self.resist = resist
        self.attacks = attacks
        self.nodeAttack = nodeAttack if nodeAttack else [False for _ in self.attacks]
        self.nodesAttacked = nodesAttacked if nodesAttacked else [0 for _ in self.attacks]
        self.attackType = attackType
        self.dodge = dodge
        self.move = move
        self.attackRange = attackRange
        self.attackEffect = attackEffect
        self.weakArcs = weakArcs if weakArcs is not None else 1 if "boss" in enemyType else 0
        self.windup = windup
        self.skip = skip
        self.difficulty = difficulty

        check = set()
        check.add(len(self.attacks))
        check.add(len(self.attackRange))
        check.add(len(self.attackType))
        check.add(len(self.nodeAttack))
        check.add(len(self.nodesAttacked))
        if self.attackEffect:
            check.add(len(self.attackEffect))
        if len(check) > 1 or set(self.attackType) - {"physical", "magic"}:
            raise
        
        if "Hollow" in self.name and self.health == 1:
            self.gang = "Hollow"
        elif "Alonne" in self.name and self.health == 1:
            self.gang = "Alonne"
        elif "Skeleton" in self.name and self.health == 1:
            self.gang = "Skeleton"
        elif "Scarecrow" in self.name:
            self.gang = "Scarecrow"
        else:
            self.gang = None

        self.deaths = 0
        self.damageDone1 = 0
        self.damageDone2 = 0
        self.damageDone3 = 0
        self.damageDone4 = 0
        self.bleedDamage1 = 0
        self.bleedDamage2 = 0
        self.bleedDamage3 = 0
        self.bleedDamage4 = 0
        self.damagingAttacks = 0
        self.totalAttacks = 0
        self.loadoutDamage = {}
        self.imagePath = baseFolder + "\\images\\" + name + ".png"

        for i, m in enumerate(self.move):
            reach.append(min([4, max([0, m + self.attackRange[i]])]))


    def reset(self):
        with open(baseFolder + "\\enemies\\" + self.name + ".json", "w") as enemyFile:
            dump({"deaths": 0, "totalAttacks": 0, "damagingAttacks": 0, "damageDone": 0, "bleedDamage": 0, "loadoutDamage": {}}, enemyFile)


